<?php /* *  © OSIREM *   *  ecoin-ECN *  cube-utility.php*/function ag_readElement(&$f_Charpntr, &$f_Cy, $iResult){	$f_Scan = true;	$f_Cn = $f_Cy;	$f_A = array();	$f_A[0] = ' ';	$f_A[1] = ' ';	$f_A[2] = ' ';	$f_A[3] = ' ';	$f_Res = "";	if(($f_Cn + 4) >= $iResult)		{		return "";		}	$f_A[0] = $f_Charpntr[$f_Cn];	$f_A[1] = $f_Charpntr[$f_Cn + 1];	$f_A[2] = $f_Charpntr[$f_Cn + 2];	$f_A[3] = $f_Charpntr[$f_Cn + 3];	$f_Cn += 4;	$f_Cy += 4;	if ($f_A[0] == ':' &&		$f_A[1] == ':' &&		$f_A[2] == ':' &&		$f_A[3] == ':')		{		return "";		}	else		{		if($f_A[0] != ':')			{			$f_Res .= $f_A[0];			}		}	while($f_Scan)		{		if(($f_Cn + 1) >= $iResult)			{			return "";			}		$f_A[0] = $f_A[1];		$f_A[1] = $f_A[2];		$f_A[2] = $f_A[3];		$f_A[3] = $f_Charpntr[$f_Cn];		$f_Cn++;		$f_Cy++;		if ($f_A[0] == ':' &&			$f_A[1] == ':' &&			$f_A[2] == ':' &&			$f_A[3] == ':')			{			$f_Scan = false;			}		else			{			if($f_A[0] != ':')				{				$f_Res .= $f_A[0];				}			}		}	return $f_Res;}?>